targets.expr:
avg by (subname) 
(
    avg by (subname, pod) (
        last_over_time(
            avg_over_time(
                (cnpg_pg_stat_subscription_received_lsn{namespace="$namespace",job="$namespace/$cluster",worker_type="apply"} - cnpg_pg_stat_subscription_latest_end_lsn < bool 500 * 1024 * 1024)[$__range]
            )[$__interval]
        )
    )
    *
    avg by (subname, pod) (
        last_over_time(
            avg_over_time(
                (time() - cnpg_pg_stat_subscription_last_msg_receipt_time {namespace="$namespace",job="$namespace/$cluster",worker_type="apply"} < bool 120)[$__range]
            )[$__interval]
        )
    )
    * 
    on (pod) group_left() (label_replace(kube_customresource_primary_info{customresource_group="postgresql.cnpg.io", customresource_kind="Cluster", namespace="$namespace",cluster="$cluster"}, "pod", "$1", "current_primary", "(.*)"))
)[7d]
